import React, { Component } from 'react';
import { Form, DatePicker, TimePicker, Button } from 'antd';

const config = {
    rules: [
      {
        type: 'object',
        required: true,
        message: 'Please select time!',
      },
    ],
};  

class BookingForm extends Component {

    onFinish = fieldsValue => {
        const values = {
            ...fieldsValue,
            'date': fieldsValue['date'].format('YYYY-MM-DD'),
            'time': fieldsValue['time'].format('HH:mm a'),
            'flexibility-before': fieldsValue['flexibility-before'].format('h:mm a'),
            'flexibility-after': fieldsValue['flexibility-after'].format('h:mm a'),
        };
        console.log('Received values of form: ', values);
    };

    render() {
        return (
            <Form name="booking-form" onFinish={this.onFinish}>
                <Form.Item name="date" label="Date" {...config}>
                    <DatePicker />
                </Form.Item>
                <Form.Item name="time" label="Time"  {...config}>
                    <TimePicker use12Hours format="h:mm a" minuteStep={15}/>
                </Form.Item>
                <Form.Item name="flexibility-before" label="Flexibility Before" {...config}>
                    <TimePicker use12Hours format="h:mm a" minuteStep={15}/>
                </Form.Item>
                <Form.Item name="flexibility-after" label="Flexibility After" {...config}>
                    <TimePicker use12Hours format="h:mm a" minuteStep={15}/>
                </Form.Item>
                <Form.Item>
                    <Button type="primary" htmlType="submit">
                        Submit
                    </Button>
                </Form.Item>
            </Form>
        );
    }
}

export default BookingForm;
