import React, { Component, useState } from 'react'
import { Form, Input } from 'antd';


class EditProfile extends Component {
    render() {
        return (
            <div>
                <Demo/>
            </div>
        )
    }
}

const formItemLayout = {
    labelCol: {
        xs: {
          span: 24,
        },
        sm: {
          span: 5,
        },
      },
    wrapperCol: {
      xs: {
        span: 24,
      },
      sm: {
        span: 16,
      },
    },

  };

const EditDetailsForm = ({  onChange, fields }) => (
  <Form
    style={{
        type:"flex", justify:"center", align:"center"
    }}
    name="EditDetails"
    fields={fields}
    onFieldsChange={(changedFields, allFields) => {
        onChange(allFields);
      }}
  >
    <Form.Item
      name="firstname"
      label="First name"
      rules={[
        {
          required: true,
          message: 'First name is required!',
        },
      ]}
    >
      <Input />
    </Form.Item>

    <Form.Item
      name="lastname"
      label="Last name"
      rules={[
        {
          required: true,
          message: 'Last name is required!',
        },
      ]}
    >
      <Input />
    </Form.Item>

    <Form.Item
      name="email"
      label="Email"
      rules={[
        {
            type: 'email',
            message: 'The input is not valid E-mail!',
        },
        {
          required: true,
          message: 'Please input your E-mail!',
        },
    ]}
    hasFeedback
    >
      <Input />
    </Form.Item>
  </Form>
);

const Demo = () => {
  const [fields, setFields] = useState([
    {
      name: ['firstname'],
      value: 'admin',
    },
    {
        name: ['lastname'],
        value: 'admin',
      },
    {
      name: ['email'],
      value: 'admin@gmail.com',
    },
    ]);
  return (
    <div>
      <EditDetailsForm
        fields={fields}
        onChange={newFields => {
          setFields(newFields);
        }}
      />
    </div>
  );
};

export default EditProfile;
