import React, { Component, useState } from 'react'
import { Form, Input } from 'antd';


class EditProfile extends Component {
    render() {
        return (
            <div>
                <Demo/>
            </div>
        )
    }
}


const CustomizedForm = ({ onChange, fields }) => (
  <Form
    name="global_state"
    layout="inline"
    fields={fields}
    onFieldsChange={(changedFields, allFields) => {
      onChange(allFields);
    }}
  >
    <Form.Item
      name="username"
      label="Username"
      rules={[
        {
          required: true,
          message: 'Username is required!',
        },
      ]}
    >
      <Input />
    </Form.Item>
  </Form>
);

const Demo = () => {
  const [fields, setFields] = useState([
    {
      name: ['userame'],
      value: 'Ant Den',
    },
  ]);
  return (
    <div>
      <CustomizedForm
        fields={fields}
        onChange={newFields => {
          setFields(newFields);
        }}
      />
    </div>
  );
};

export default EditProfile;
