import React, { Component } from 'react'
import axios from "axios";

import { getConfig } from "../utils/getConfig";
import NotAuthorizedPage from './NotAuthorizedPage';
import NotFoundPage from "./NotFoundPage"
import { Spin } from 'antd';


const Display = (state) => {
    const {user, errorStatus, loading} = state

    if(loading) { return (
        <div style={Load}><Spin size="large"/></div>
    ) 
    }

    if(errorStatus) {
        if(errorStatus === 404) return <NotFoundPage/>
        if(errorStatus === 401) return <NotAuthorizedPage/>
    }

    return (
        <div>
        <h1 style={style}>{user.username}'s Profile</h1>
        <h3>Name:</h3> <p>{user.first_name + " " + user.last_name} </p>
        <h3>Email:</h3> <p>{user.email}</p> 
        <h3>Phone number:</h3> <p>{user.phone} </p>
        </div>
    ); 
}

class UserProfile extends Component {

    state = {
        user: [],
        loading: false,
        errorStatus: null
    };

    componentDidMount() {
        const username = this.props.match.params.username;
        this.setState({ loading: true })
        axios.get(`http://127.0.0.1:8000/users/${username}`, getConfig())
            .then((res) => {
                this.setState({
                    user: res.data,
                    errorStatus: null,
                    loading: false,
                });
            })
            .catch((err) => {
                this.setState({
                    errorStatus: err.response.status,
                    loading: false,
                })
            });
    };

    componentDidUpdate(prevProps) {
        const username = this.props.match.params.username;
        if (username !== prevProps.match.params.username) {
        axios.get(`http://127.0.0.1:8000/users/${username}`, getConfig())
            .then((res) => {
                this.setState({
                    user: res.data,
                    errorStatus: null,
                    loading: false,
                });
            })
            .catch((err) => {
                this.setState({
                    errorStatus: err.response.status,
                    loading: false
                })
            });
        }
    }

    render() {

        return (
            <div>
                <Display {...this.state}/>
            </div>
        )
    }
}

/* TODO: set styling properly */
const Load = {
    textAlign: 'center',
    borderRadius: '4px',
    marginBottom: '20px',
    padding: '30px 50px',
    margin: '20px 0',
}

const style = {
    textAlign: 'center'
}

export default UserProfile
