import React, { Component, useState } from 'react'
import { Form, Input } from 'antd';
import axios from "axios";
import { getConfig } from "../utils/getConfig";

class EditProfile extends Component {

    state = {
        user: [],
        loading: true,
    }

    componentDidMount() {
        const uname = this.props.match.params.uname;
        axios.get(`http://127.0.0.1:8000/users/${uname}`, getConfig())
            .then((res) => {
                this.setState({
                    user: res.data,
                    loading: false,
                });
            })
            .catch((err) => {
                console.log(err);
                this.setState({
                    loading: false,
                });
            });
    };

    render() {
        const data = {
            first_name: this.state.user.first_name,
            last_name: this.state.user.last_name,
            email: this.state.user.email
        }
        return (
            <div> 
            {
                this.state.loading ? null :
                <Form
                    name="EditDetails"
                    layout="vertical"
                    initialValues={data}
                >
                    <Form.Item
                        name="first_name"
                        label="First name"
                        rules={[
                            {
                                required: true,
                                message: 'First name is required!',
                            },
                        ]}
                    >
                        <Input />
                    </Form.Item>

                    <Form.Item
                        name="last_name"
                        label="Last name"
                        rules={[
                            {
                                required: true,
                                message: 'Last name is required!',
                            },
                        ]}
                    >
                        <Input />
                    </Form.Item>

                    <Form.Item
                        name="email"
                        label="Email"
                        rules={[
                            {
                                type: 'email',
                                message: 'The input is not valid E-mail!',
                            },
                            {
                                required: true,
                                message: 'Please input your E-mail!',
                            },
                        ]}
                        hasFeedback
                    >
                        <Input />
                    </Form.Item>
                </Form>
            }
            </div>
        )
    }
}


export default EditProfile;
