import store from '../storeRedux';

export const getConfig = () => {
  const isAuthenticated = store.getState().isAuthenticated;
  if (isAuthenticated) {
    const token = store.getState().token;
    const config = {
      headers: {Authorization: 'Token ' + token},
    };
    console.log(config)
    return config;
  }
  return null;
};
