import React, { Component } from 'react';
import { Table, Divider, Button } from 'antd';
import AddBooking from "../components/AddBooking";
import axios from "axios";
import { BOOKING_URL } from "../api/constants";
import { getConfig } from "../utils/getConfig";
import { ErrorHandler } from "../utils/ErrorHandler";

const { Column } = Table;

class Booking extends Component {

  state = {
    bookings: [],
    error: null
  }

  componentDidMount() {
    axios.get(BOOKING_URL, getConfig())
      .then(res => {
        this.setState({
          bookings: res.data
        })
      })
      .catch(err => {
        this.setState({
          error: ErrorHandler(err)
        })
      })
  }

  onDelete = (id) => {
    axios.delete(BOOKING_URL + `details/${id}`, getConfig())
    .then(res => {
      console.log(res)
    })
    .catch(err => {
      this.setState({
        error: ErrorHandler(err)
      })
    })
  }

  render() {
    {console.log(this.state.bookings)}
    return(
      <div>
        <h3>{this.state.error}</h3>
          <AddBooking/>
          <div style={{marginBottom: "15px"}}></div>
          <Table dataSource={this.state.bookings} rowKey="id">
            <Column title="User" dataIndex="user" key="user" />
            <Column title="Date" dataIndex="date" key="date" />
            <Column title="Time" dataIndex="time" key="time" />
            <Column title="Flexibility Before" dataIndex="flexibility_before" key="flexibility_before" />
            <Column title="Flexibility After" dataIndex="flexibility_after" key="flexibility_after" />
            <Column
            title="Action"
            key="action"
            render={(text, record) => (
                <span>
                <a href="/">Edit</a>  
                <Divider type="vertical" />
                <Button danger onClick={this.onDelete(record.id)}>Delete</Button>
                </span>
            )}
            />
        </Table>  
      </div>
    );
  }
}

export default Booking;
