import React, { Component } from 'react';
import { Form, DatePicker, TimePicker, Button } from 'antd';

import moment from 'moment';

const config = {
    rules: [
      {
        type: 'object',
        required: true,
        message: 'Please select time!',
      },
    ],
};  

const disabledDate = (current) => {
    return current && current < moment().startOf('day');
}

class BookingForm extends Component {

    state = {
        value: null,
    };

    onChange = time => {
        this.setState({ value: time });
    };


    onFinish = fieldsValue => {
        const values = {
            ...fieldsValue,
            'date': fieldsValue['date'].format('YYYY-MM-DD'),
            'time': fieldsValue['time'].format('hh:mm a'),
            'flexibility-before': fieldsValue['flexibility-before'].format('hh:mm a'),
            'flexibility-after': fieldsValue['flexibility-after'].format('hh:mm a'),
        };
        console.log('Received values of form: ', values);
    };

    render() {
        return (
            <Form name="booking-form" onFinish={this.onFinish}>

                <Form.Item name="date" label="Date" {...config}>
                    <DatePicker disabledDate={disabledDate}/>
                </Form.Item>

                <Form.Item name="time" label="Time"  {...config}>
                    <TimePicker focusOnOpen use12Hours format="hh:mm a" minuteStep={15} value={this.state.value} onChange={this.onChange}/>
                </Form.Item>

                <Form.Item name="flexibility-before" label="Flexibility Before" {...config}>
                    <TimePicker use12Hours format="hh:mm a" minuteStep={15} value={this.state.value} onChange={this.onChange}/>
                </Form.Item>

                <Form.Item name="flexibility-after" label="Flexibility After" {...config}>
                    <TimePicker use12Hours format="hh:mm a" minuteStep={15} value={this.state.value} onChange={this.onChange}/>
                </Form.Item>

                <Form.Item>
                    <Button type="primary" htmlType="submit">
                        Submit
                    </Button>
                </Form.Item>
            </Form>
        );
    }
}

export default BookingForm;
