import React, { Component } from 'react'
import axios from "axios";

import { getConfig } from "../utils/getConfig";
import NotAuthorizedPage from './NotAuthorizedPage';
import NotFoundPage from "./NotFoundPage"

class UserProfile extends Component {

    state = {
        user: [],
        errorStatus: null
    };

    componentDidMount() {
        const username = this.props.match.params.username;
        axios.get(`http://127.0.0.1:8000/users/${username}`, getConfig())
            .then((res) => {
                this.setState({
                    user: res.data,
                });
            })
            .catch((err) => {
                this.setState({
                    errorStatus: err.response.status,
                })
            });
    };

    componentDidUpdate(prevProps) {
        const username = this.props.match.params.username;
        if (username !== prevProps.match.params.username) {
        axios.get(`http://127.0.0.1:8000/users/${username}`, getConfig())
            .then((res) => {
                this.setState({
                    user: res.data,
                });
            })
            .catch((err) => {
                this.setState({
                    errorStatus: err.response.status,
                })
            });
        }
    }

    render() {

        return (
            <div>
                {(() => {
                    switch (this.state.errorStatus) {
                        case 404: 
                            return <NotFoundPage/>
                        case 401: 

                            return <NotAuthorizedPage/>
                        default:
                            return (
                            <div>
                            <h1 style={style}>{this.state.user.username}'s Profile</h1>
                            <h3>Name:</h3> <p>{this.state.user.first_name + " " + this.state.user.last_name} </p>
                            <h3>Email:</h3> <p>{this.state.user.email}</p> 
                            <h3>Phone number:</h3> <p>{this.state.user.phone} </p>
                            </div>
                            ); 
                        }
                    }
                )()
                }
            </div>
        )
    }
}

const style = {
    textAlign: 'center'
}

export default UserProfile
