import React, { Component } from 'react';
import { Table, Divider } from 'antd';
import AddBooking from "../components/AddBooking";
import axios from "axios";

const { Column } = Table;

class Booking extends Component {

  state = {
    bookings: []
  }

  componentDidMount() {
    axios.get("http://127.0.0.1:8000/booking/")
      .then(res => {
        this.setState({
          bookings: res.data
        })
      })
  }

  render() {
    {console.log(this.state.bookings)}
    return(
      <div>
          <AddBooking/>
          <div style={{marginBottom: "15px"}}></div>
          <Table dataSource={this.state.bookings} rowKey="id">
            <Column title="User" dataIndex="user" key="user" />
            <Column title="Date" dataIndex="date" key="date" />
            <Column title="Time" dataIndex="time" key="time" />
            <Column title="Flexibility Before" dataIndex="flexibility_before" key="flexibility_before" />
            <Column title="Flexibility After" dataIndex="flexibility_after" key="flexibility_after" />
            <Column
            title="Action"
            key="action"
            render={(text, record) => (
                <span>
                <a href="/">Edit</a>  
                <Divider type="vertical" />
                <a href="/">Delete</a>
                </span>
            )}
            />
        </Table>  
      </div>
    );
  }
}

export default Booking;
