import React, { Component } from 'react'
import { Link, withRouter } from 'react-router-dom';

import { Layout, Menu, Breadcrumb } from 'antd';
import "../static/MainLayout.css";
import { connect } from "react-redux";
import * as actions from "../store/actions/auth";

const { Header, Content, Footer } = Layout;

const breadcrumbNameMap = {
    '/login'    : 'Login',
    '/register' : 'Register',
    '/booking'  : 'Booking',  
}

const BreadCrumbComponent = withRouter( props => {
    const { location } = props;
    const pathSnippets = location.pathname.split('/').filter(i => i);

    const extraBreadcrumbItems = pathSnippets.map( (_, index) => {
        const url = `/${pathSnippets.slice(0, index + 1).join('/')}`;
        return (
            <Breadcrumb.Item key={url}>
                <Link to={url}>{breadcrumbNameMap[url]}</Link>
            </Breadcrumb.Item>
        );
    });

    const breadcrumbItems = [
        <Breadcrumb.Item key="/">
            <Link to="/">Home</Link>
        </Breadcrumb.Item>,
    ].concat(extraBreadcrumbItems);

    return (
      <div className="crumb">
        <Breadcrumb>{breadcrumbItems}</Breadcrumb>
      </div>
    );
});

const Location = withRouter( props => {
    const { location, logout, isLoggedIn } = props;

    return (
      <Menu
        theme="dark"
        mode="horizontal"
        selectedKeys={[location.pathname]}
        style={{ lineHeight: '64px' }}
      >
        <Menu.Item key="/"><a href="/">Home</a></Menu.Item>
        {
            isLoggedIn ?
            [
                <Menu.Item key="/booking">Add Booking</Menu.Item>,
                <Menu.Item key="/logout" onClick={logout}>Logout</Menu.Item>
            ]
            :
            [
                <Menu.Item key="/login"><a href="/login">Login</a></Menu.Item>,
                <Menu.Item key="/register"><a href="/register">Register</a></Menu.Item>
            ]
        }
      </Menu>
    )
})

const WelcomeUser = props => {
    if(props.username){
    return (
        <div>
            <h1>Welcome to PoolIIIT <b>{props.username}</b> !!</h1>
        </div>
    )}
    return null    
}

class MainLayout extends Component {
    render() {
        return (
            <div>
                <Layout className="layout">

                    <Header>
                        <div className="logo">
                            <h1>PoolIIIT</h1>
                        </div>
                        <Location {...this.props}/>
                    </Header>

                    <Content style={{ padding: '0 50px' }}>
                        <BreadCrumbComponent />
                        <WelcomeUser {...this.props}/>
                        <div style={{ background: '#fff', padding: 24, minHeight: 280 }}>
                            {this.props.children}
                        </div>
                    </Content>

                    <Footer style={{ textAlign: 'center' }}>Created by Zense</Footer>

                </Layout>                
            </div>
        )
    }
}

const mapStateToProps = (state) => {
    return {
        isAuthenticated: state.isAuthenticated,
        username: state.username
    }
}

const mapDispatchToProps = (dispatch) => {
    return {
        logout: () => dispatch(actions.logout())
    }
}

export default withRouter(connect(mapStateToProps, mapDispatchToProps)(MainLayout));
