import React, { Component } from 'react'
import axios from "axios";

import { getConfig } from "../utils/getConfig";
import NotAuthorizedPage from './NotAuthorizedPage';
import NotFoundPage from "./NotFoundPage"


const display = (state) => {
    const {user, errorStatus} = state
    console.log(user, errorStatus)
        switch (errorStatus) {
            case 404: 
                return <NotFoundPage/>
            case 401:                             
                return <NotAuthorizedPage/>
            default:
                return (
                <div>
                <h1 style={style}>{user.username}'s Profile</h1>
                <h3>Name:</h3> <p>{user.first_name + " " + user.last_name} </p>
                <h3>Email:</h3> <p>{user.email}</p> 
                <h3>Phone number:</h3> <p>{user.phone} </p>
                </div>
                ); 
            }
}

class UserProfile extends Component {

    state = {
        user: [],
        errorStatus: null
    };

    componentDidMount() {
        const username = this.props.match.params.username;
        axios.get(`http://127.0.0.1:8000/users/${username}`, getConfig())
            .then((res) => {
                this.setState({
                    user: res.data,
                    errorStatus: null
                });
            })
            .catch((err) => {
                this.setState({
                    errorStatus: err.response.status,
                })
            });
    };

    componentDidUpdate(prevProps) {
        const username = this.props.match.params.username;
        if (username !== prevProps.match.params.username) {
        axios.get(`http://127.0.0.1:8000/users/${username}`, getConfig())
            .then((res) => {
                this.setState({
                    user: res.data,
                    errorStatus: null
                });
            })
            .catch((err) => {
                this.setState({
                    errorStatus: err.response.status,
                })
            });
        }
    }

    render() {

        return (
            <div>
                <display {...this.state}/>
            </div>
        )
    }
}

const style = {
    textAlign: 'center'
}

export default UserProfile
